import './App.css';
import React from 'react'
import Models from './Models'
import TrainingFileUploadBox  from "./TrainingFileUploadBox";
import convertCSVToJSON, { splitCSV } from './utils'
import AnomalyFileUploadBox from './AnomalyFileUploadBox';
import Table from './Table';

class App extends React.Component {

  constructor(props) {
    super(props);

    this.state = {
      FlightDataTable: new Table({data : []})
    }
  }

  render() {
    return (
      <div>
        <AnomalyFileUploadBox onUpload={file => splitCSV(file)
          .then(lines => this.setState({ FlightDataTable: new Table(lines) }))} />

        <div style={{ position: 'fixed', width: '80%', height: '30%', overflowY: 'scroll',  bottom: '0%', right: '18%' }}>
            {this.state.FlightDataTable}
        </div>

        <div style={{ position: 'fixed', width: '20%', overflowY: 'scroll', top: 10, bottom: '20%', left: '83%' }}>
          <Models />
        </div>

        <div style={{ position: 'fixed', bottom: '5%', right: '2%' }} >
          <TrainingFileUploadBox onUpload={(file, algorithm) => 
          convertCSVToJSON(file)
            .then(json => fetch("/api/model/" + algorithm, {
              method: 'POST',
              headers: { 'Content-Type': 'application/json' },
              body: json
            }))} />
        </div>
      </div>
    );
  }
}

export default App;
